import { useDispatch, useSelector } from "react-redux";

import { useHistory } from "react-router-dom";
import { useState } from "react";
import { userAdded } from "./usersSlice";

export function AddUser() {
  const dispatch = useDispatch();
  const history = useHistory();

  const [name, setName] = useState("");
  const [rank, setRank] = useState("");
  const [error, setError] = useState(null);

  const handleName = (e) => setName(e.target.value);
  const handleRank = (e) => setRank(e.target.value);

  const usersAmount = useSelector((state) => state.users.entities.length);

  const handleClick = () => {
    if (name && rank) {
      dispatch(
        userAdded({
          id: usersAmount + 1,
          name,
          rank,
        })
      );

      setError(null);
      history.push("/");
    } else {
      setError("Fill in all fields");
    }

    setName("");
    setRank("");
  };

  return (
    <div className="container">
      <div className="row">
        <h1>Add Country</h1>
      </div>
      <div className="row">
        <div className="three columns">
          <label htmlFor="continentInput">continent</label>
          <select value={selectedOption} onChange={(e) => handleCountryById(e.target.value)} >
            <option
              key={0}
