              {"Select One"}
            </option>
            {entities.length &&
              entities.map(({ id, name }, i) => (
                <option
                  key={id}
                  value={id}
                >
                  {name}
                </option>
              ))}
          </select>
          <label htmlFor="nameInput">Name</label>
          <input
            className="u-full-width"
            type="text"
            id="nameInput"
            onChange={handleName}
            value={name}
          />
          <label htmlFor="rankInput">Rank</label>
          <input
            className="u-full-width"
            type="rank"
            id="rankInput"
            onChange={handleRank}
            value={rank}
          />
          {error && error}
          <button onClick={handleClick} className="button-primary">
            Add user
          </button>
        </div>
      </div>
    </div>
  );
}
