import { fetchUsers, userDeleted } from "./usersSlice";
import { useDispatch, useSelector } from "react-redux";
import React, { useState } from "react";


import { Link } from "react-router-dom";

export function UserList() {
  const dispatch = useDispatch();

  const { entities } = useSelector((state) => state.users);
  const loading = useSelector((state) => state.loading);

  const handleDelete = (id) => {
    dispatch(userDeleted({ id }));
  };

  const [selectedOption, setSelectedOption] = useState("1");

  const handleCountryById = (id) => {
    setSelectedOption(id)
    dispatch(fetchUsersById(id));
  };

  return (
    <div className="container">
      <div className="row">
        <h1>CRUD Country</h1>
      </div>
      <div className="row">
        <div className="two columns">
          <select value={selectedOption} onChange={(e) => handleCountryById(e.target.value)} >
            {entities.length &&
              entities.map(({ id, name }, i) => (
                <option
                  key={id}
                  value={name}
                >
                  {name}
                </option>
              ))}
          </select>
          {/* <button
            onClick={() => dispatch(fetchUsers())}
            className="button-primary"
          >
            Load users
          </button> */}
        </div>
        <div className="two columns">
          <Link to="/add-user">
            <button className="button-primary">Add Country</button>
          </Link>
        </div>
      </div>
      <div className="row">
        {loading ? (
          "Loading..."
        ) : (
          <table className="u-full-width">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {entities.length &&
                entities.map(({ id, name }, i) => (
                  <tr key={i}>
                    <td>{id}</td>
                    <td>{name}</td>
                    <td>
                      {/* <button onClick={() => handleDelete(id)}>Delete</button> */}
                      <Link to={`/edit-user/${id}`}>
                        <button>Edit</button>
                      </Link>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
