import { useDispatch, useSelector } from "react-redux";
import ImageUploader from "react-images-upload";
import { useHistory } from "react-router-dom";
import { useState } from "react";
import { userAdded } from "./usersSlice";

export function AddUser() {
  const dispatch = useDispatch();
  const history = useHistory();

  const [name, setName] = useState("");
  const [rank, setRank] = useState("");
  const [error, setError] = useState(null);

  const handleName = (e) => setName(e.target.value);
  const handleRank = (e) => setRank(e.target.value);

  const { entitiesContinent, errors } = useSelector((state) => state.users);


  const handleClick = async () => {
    // if (name && rank) {
    const data = new FormData();

    data.append("name", name)
    data.append("continent", continent)
    data.append("rank", rank)
    data.append('file', file);
    await dispatch(
      userAdded({
        formData: data
      })
    );
    console.log("-----------------------------------", errors)
    if (errors.length) {
      alert("hee")
    } else {
      setError(null);
      history.push("/");
    }

    // } else {
    //   setError("Fill in all fields");
    // }

    setName("");
    setRank("");
  };

  const [continent, setContinent] = useState("");

  const [file, setFile] = useState("");

  const onDrop = picture => {
    console.log(picture[0])
    setFile(picture[0]);
  };

  return (
    <div className="container">
      <div className="row">
        <h1>Add Country</h1>
      </div>
      <div className="row">
        <div className="three columns">
          <label htmlFor="nameInput">Name</label>
          <input
            className="u-full-width"
            name="name"
            type="text"
            id="nameInput"
            onChange={handleName}
            value={name}
          />
          <label htmlFor="continentInput">Continent</label>
          <select name="continent" value={continent} onChange={(e) => setContinent(e.target.value)} >
            <option
              key={0}
              value={0}
            >
              {"Select One"}
            </option>
            {entitiesContinent.length &&
              entitiesContinent.map((x, i) => (
                <option
                  key={i}
                  value={x}
                >
                  {x}
                </option>
              ))}
          </select>

          <label htmlFor="rankInput">Rank</label>
          <input
            name="rank"
            className="u-full-width"
            type="rank"
            id="emailInput"
            onChange={handleRank}
            value={rank}
          />
          <ImageUploader
            withIcon={true}
            onChange={onDrop}
          //  imgExtension={[".jpg", ".gif", ".png", ".gif"]}
          // maxFileSize={5242880}
          />
          {error && error}
          <button onClick={handleClick} className="button-primary">
            Add user
          </button>
        </div>
      </div>
    </div>
  );
}
