import { useDispatch, useSelector } from "react-redux";
import ImageUploader from "react-images-upload";
import { useHistory } from "react-router-dom";
import { useState } from "react";
import { userAdded } from "./usersSlice";

export function AddUser() {
  const dispatch = useDispatch();
  const history = useHistory();

  const [name, setName] = useState("");
  const [rank, setRank] = useState("");
  const [error, setError] = useState("");
  const [formErrors, setFormErrors] = useState({});

  const handleName = (e) => setName(e.target.value);
  const handleRank = (e) => setRank(e.target.value);

  const { entitiesContinent } = useSelector((state) => state.users);


  const handleClick = () => {
    // if (name && rank) {
    const data = new FormData();

    data.append("name", name)
    data.append("continent", continent)
    data.append("rank", rank)
    data.append('file', file);
    fetch('http://localhost:8080/api/country', { method: 'POST', body: data }).then(response => response.json())
      .then(result => {
        console.log('Success:', result.error);
        if (result.error.length) {
          setFormErrors(result.error[0])
        } else {
          //setError(null);
          // history.push("/");
        }
      })
      .catch(error => {
        console.error('Error:', error);
      });
    setName("");
    setRank("");
  };

  const [continent, setContinent] = useState("");

  const [file, setFile] = useState("");

  const onDrop = picture => {
    console.log(picture[0])
    setFile(picture[0]);
  };

  return (
    <div className="container">
      <div className="row">
        <h1>Add Country</h1>
      </div>
      <div className="row">
        <div className="three columns">
          <label htmlFor="nameInput">Name</label>
          <input
            className="u-full-width"
            name="name"
            type="text"
            id="nameInput"
            onChange={handleName}
            value={name}
          />
          {formErrors.name && formErrors.name}
          <label htmlFor="continentInput">Continent</label>
          <select name="continent" value={continent} onChange={(e) => setContinent(e.target.value)} >
            <option
              key={0}
              value={0}
            >
              {"Select One"}
            </option>
            {entitiesContinent.length &&
              entitiesContinent.map((x, i) => (
                <option
                  key={i}
                  value={x}
                >
                  {x}
                </option>
              ))}
          </select>
          {formErrors.continent && formErrors.continent}

          <label htmlFor="rankInput">Rank</label>
          <input
            name="rank"
            className="u-full-width"
            type="rank"
            id="emailInput"
            onChange={handleRank}
            value={rank}
          />
          {formErrors.rank && formErrors.rank}
          <ImageUploader
            withIcon={true}
            onChange={onDrop}
          //  imgExtension={[".jpg", ".gif", ".png", ".gif"]}
          // maxFileSize={5242880}
          />
          {formErrors.file && formErrors.file}
          {error && error}
          <button onClick={handleClick} className="button-primary">
            Add user
          </button>
        </div>
      </div>
    </div>
  );
}
